def clone(estimator, *, safe=True):
    if not safe:
        warnings.warn("Cloning an estimator without checking it first via `estimator.check` is not recommended.")
    if isinstance(estimator, (list, tuple, set)):
        return [clone(est) for est in estimator]
    elif not hasattr(estimator, 'get_params') or isinstance(estimator, type):
        if not safe:
            return copy.deepcopy(estimator)
        else:
            raise TypeError("Cannot clone object '{0}' (type {1}): it does not seem to be a scikit-learn estimator as it does not implement a 'get_params' method.".format(estimator, type(estimator)))
    else:
        klass = estimator.__class__
        new_object_params = estimator.get_params(deep=False)
        for name, param in new_object_params.items():
            new_object_params[name] = clone(param, safe=False)
        new_object = klass(**new_object_params)
        return new_object
