import shapely
import matplotlib.pyplot as plt
from matplotlib.path import Path
from matplotlib.patches import PathPatch
from matplotlib import colors


def plot_polygon(polygon, ax=None, add_points=True, color=None, facecolor=None, edgecolor=None, linewidth=None, **kwargs):
    if ax is None:
        fig, ax = plt.subplots()

    if color is None:
        color = 'C0'

    if facecolor is None:
        facecolor = color + '30'  # Adding alpha for fill color

    if edgecolor is None:
        edgecolor = color

    if linewidth is None:
        linewidth = 1.0

    if isinstance(polygon, shapely.geometry.MultiPolygon):
        for poly in polygon.geoms:
            patch = PathPatch(poly.exterior, facecolor=facecolor, edgecolor=edgecolor, linewidth=linewidth, **kwargs)
            ax.add_patch(patch)
            if add_points:
                ax.plot(*poly.exterior.xy, 'o', color=edgecolor)
    else:
        patch = PathPatch(polygon.exterior, facecolor=facecolor, edgecolor=edgecolor, linewidth=linewidth, **kwargs)
        ax.add_patch(patch)
        if add_points:
            ax.plot(*polygon.exterior.xy, 'o', color=edgecolor)

    ax.autoscale_view()

    if add_points:
        return patch, ax.lines[-1]  # Return the patch and the last line (points)
    else:
        return patch  # Return only the patch

