def rotate(geom, angle, origin='center', use_radians=False):
    if not use_radians:
        angle = angle * (pi / 180)  # Convert degrees to radians

    # Determine the origin of rotation
    if origin == 'center':
        # Get the bounding box center
        minx, miny, maxx, maxy = geom.bounds
        origin_point = Point((minx + maxx) / 2, (miny + maxy) / 2)
    elif origin == 'centroid':
        # Get the centroid of the geometry
        origin_point = geom.centroid
    elif isinstance(origin, Point):
        origin_point = origin
    elif isinstance(origin, tuple) and len(origin) == 2:
        origin_point = Point(origin)
    else:
        raise ValueError("Invalid origin specified.")

    # Rotate the geometry
    cos_angle = cos(angle)
    sin_angle = sin(angle)

    # Function to rotate a point around another point
    def rotate_point(point):
        x, y = point.x, point.y
        ox, oy = origin_point.x, origin_point.y
        # Translate point back to origin
        x -= ox
        y -= oy
        # Rotate point
        new_x = x * cos_angle - y * sin_angle
        new_y = x * sin_angle + y * cos_angle
        # Translate point back
        return Point(new_x + ox, new_y + oy)

    # Apply rotation to all coordinates of the geometry
    if geom.is_empty:
        return geom

    if geom.geom_type == 'Point':
        return rotate_point(geom)
    elif geom.geom_type in ['LineString', 'LinearRing']:
        return shapely.geometry.LineString([rotate_point(Point(pt)) for pt in geom.coords])
    elif geom.geom_type == 'Polygon':
        exterior = shapely.geometry.LinearRing([rotate_point(Point(pt)) for pt in geom.exterior.coords])
        interiors = [shapely.geometry.LinearRing([rotate_point(Point(pt)) for pt in interior.coords]) for interior in geom.interiors]
