def reset_signals(handler=_shutdown_cleanup, full=False):
    TERMSIGS_DEFAULT = (signal.SIGINT, signal.SIGTERM)
    TERMSIGS_FULL = tuple(getattr(signal, sig) for sig in dir(signal) if sig.startswith('SIG') and sig[3:].isdigit())

    def maybe_setsignal(signum, handler):
        if full or signum in TERMSIGS_DEFAULT:
            signal.signal(signum, handler)

    for sig in TERMSIGS_DEFAULT if not full else TERMSIGS_FULL:
        maybe_setsignal(sig, handler)
