import pyarrow.fs
import warnings

def translate_options(fs_options):
    unsupported_keys = ['role_arn', 'session_name', 'external_id', 'load_frequency', 'background_writes']
    client_kwargs_mapping = {'profile': 'profile_name', 'access_key': 'key', 'secret_key': 'secret', 'session_token': 'token'}

    translated_options = {}

    for key, value in fs_options.items():
        if key in unsupported_keys:
            warnings.warn(f"Key '{key}' is not supported by s3fs and will be ignored.")
        elif key in client_kwargs_mapping:
            translated_options[client_kwargs_mapping[key]] = value
        elif key == 'endpoint_override':
            if value.startswith('http://') or value.startswith('https://'):
                translated_options[key] = value
            else:
                translated_options[key] = f"https://{value}"
        elif key == 'scheme':
            pass  # Do nothing with 'scheme' key
        elif key == 'anonymous':
            pass  # Do nothing with 'anonymous' key
        elif key == 'region':
            translated_options['region_name'] = value
        elif key == 'client_kwargs':
            translated_options.update(value)
        else:
            translated_options[key] = value

    return translated_options

