def unite_base(dshapes):
    def is_null(dshape):
        return dshape == 'null'

    def is_option(dshape):
        return dshape.startswith('Option')

    def common_prefix(s1, s2):
        i = 0
        while i < len(s1) and i < len(s2) and s1[i] == s2[i]:
            i += 1
        return s1[:i]

    def common_dshape(dshape1, dshape2):
        if is_null(dshape1) or is_null(dshape2):
            return 'null'
        if is_option(dshape1) or is_option(dshape2):
            return 'Option[' + common_dshape(dshape1[7:-1], dshape2[7:-1]) + ']'
        if dshape1 == dshape2:
            return dshape1
        if '[' in dshape1 and '[' in dshape2:
            prefix = common_prefix(dshape1, dshape2)
            if prefix.endswith('['):
                return prefix + common_dshape(dshape1[len(prefix):-1], dshape2[len(prefix):-1]) + ']'
        return 'Any'

    if len(dshapes) == 0:
        return 'Any'
    if len(dshapes) == 1:
        return dshapes[0]

    result = dshapes[0]
    for dshape in dshapes[1:]:
        result = common_dshape(result, dshape)

    return result

def unite_base(dshapes):
    def is_nullable(dshape):
        return dshape.endswith('?')

    def remove_nullable(dshape):
        return dshape[:-1] if is_nullable(dshape) else dshape

    def add_nullable(dshape):
        return dshape + '?' if not is_nullable(dshape) else dshape

    def common_prefix(strings):
        return os.path.commonprefix(strings)

    def common_dshape(dshapes):
        if all(is_nullable(dshape) for dshape in dshapes):
            return add_nullable(common_dshape([remove_nullable(dshape) for dshape in dshapes]))
        if all(dshape == dshapes[0] for dshape in dshapes):
            return dshapes[0]
        if all(dshape.startswith('record') for dshape in dshapes):
            fields = [re.findall(r'\w+', dshape) for dshape in dshapes]
            common_fields = common_prefix(fields)
            return 'record[{}]'.format(','.join(common_fields))
        return 'any'

    return common_dshape(dshapes)

def unite_base(dshapes):
    common_dtype = np.find_common_type([np.dtype(dshape) for dshape in dshapes], [])
    if any('?' in str(np.dtype(dshape)) for dshape in dshapes):
        return np.dtype('O')
    return common_dtype

def unite_base(dshapes):
    def is_nullable(dshape):
        return 'null' in dshape

    def remove_null(dshape):
        return dshape.replace('null', '')

    def add_null(dshape):
        return 'null' + dshape

    def common_dshape(dshapes):
        common = dshapes[0]
        for dshape in dshapes[1:]:
            if common == dshape:
                continue
            common = ''
            for i, (c, d) in enumerate(zip(common, dshape)):
                if c == d:
                    common += c
                else:
                    break
        return common

    if all(is_nullable(dshape) for dshape in dshapes):
        return add_null(common_dshape([remove_null(dshape) for dshape in dshapes]))
    else:
        return common_dshape(dshapes)
