def default_fill_value(obj):
    if isinstance(obj, bool_):
        return True
    elif isinstance(obj, (int, np.integer)):
        return 999999
    elif isinstance(obj, (float, np.floating)):
        return 1.e20
    elif isinstance(obj, (complex, np.complexfloating)):
        return 1.e20 + 0j
    elif isinstance(obj, str):
        return 'N/A'
    elif isinstance(obj, (ndarray, np.dtype)):
        if np.issubdtype(obj, np.void):
            return np.zeros(obj.shape, dtype=obj)
        elif np.issubdtype(obj, np.object_):
            return '?'
        elif np.issubdtype(obj, np.number):
            return default_fill_value(np.dtype(obj).type())
        else:
            return default_fill_value(np.dtype(obj))
    else:
        return '?'

