def notmasked_edges(a, axis=None):
    masked_array = np.ma.masked_array(a)

    if axis is None:
        flattened = masked_array.flatten()
        first_unmasked = np.ma.nonzero(~flattened.mask)[0]
    else:
        first_unmasked = np.ma.nonzero(~masked_array.mask, axis=axis)[0]

    if first_unmasked.size == 0:
        return None

    first_index = first_unmasked[0]
    last_index = first_unmasked[-1]

    if axis is None:
        return [(first_index, last_index)]
    else:
        return [(first_index, last_index)]

