def compress_nd(x, axis=None):
    if not isinstance(x, np.ma.MaskedArray):
        x = np.ma.masked_array(x)

    if axis is None:
        # Compress along all axes
        compress_array = x.compressed()
        return compress_array.reshape(-1)

    if isinstance(axis, int):
        axis = (axis,)

    # Create a list of axes to compress
    for ax in axis:
        if ax < 0:
            ax += x.ndim
        if ax < 0 or ax >= x.ndim:
            raise ValueError(f"Axis {ax} is out of bounds for array of dimension {x.ndim}")

    # Compress the array along the specified axes
    compress_array = x
    for ax in sorted(axis, reverse=True):
        compress_array = np.ma.compress_rows(compress_array, axis=ax)

    return compress_array

