def compress_rowcols(x, axis=None):
    if not isinstance(x, np.ma.MaskedArray):
        x = np.ma.masked_array(x, mask=np.ma.getmaskarray(x) if np.ma.isMaskedArray(x) else np.zeros_like(x, dtype=bool))

    if axis is None:
        compressed_array = x.compressed()
    elif axis == 0:
        compressed_array = x[~x.mask.any(axis=1)]
    elif axis in (1, -1):
        compressed_array = x[:, ~x.mask.any(axis=0)]
    else:
        raise ValueError("Invalid axis value. It must be None, 0, 1, or -1.")

    return compressed_array

