def mask_rowcols(a, axis=None):
    if not isinstance(a, np.ma.MaskedArray):
        raise ValueError("Input must be a MaskedArray.")

    if a.ndim != 2:
        raise NotImplementedError("Input array must be 2D.")

    if a.mask is False:
        return a  # No elements are masked, return as is

    if axis is None:
        # Mask both rows and columns
        a.mask |= np.any(a.mask, axis=0)[None, :]  # Mask columns
        a.mask |= np.any(a.mask, axis=1)[:, None]  # Mask rows
    elif axis == 0:
        # Mask only rows
        a.mask |= np.any(a.mask, axis=1)[:, None]  # Mask rows
    elif axis == 1 or axis == -1:
        # Mask only columns
        a.mask |= np.any(a.mask, axis=0)[None, :]  # Mask columns
    else:
        raise ValueError("Invalid axis value. Must be None, 0, 1, or -1.")

    return a

