def easy_dtype(ndtype, names=None, defaultfmt='f%i', **validationargs):
    if isinstance(ndtype, str) or isinstance(ndtype, dict):
        dtype = np.dtype(ndtype)
    else:
        dtype = np.dtype(list(ndtype))

    if names is not None:
        if isinstance(names, str):
            names = names.split(',')
        if len(names) != dtype.fields:
            names = [defaultfmt % i for i in range(len(dtype.fields))]

        fields = [(name, dtype[i]) for i, name in enumerate(names)]
        dtype = np.dtype({'names': names, 'formats': [dtype[i] for i in range(len(names))]})

    return dtype

