def flatten_dtype(ndtype, flatten_base=False):
    flattened = []

    if ndtype.subdtype is not None:
        # Handle subdtype (structured array)
        for name, sub_dtype in ndtype.fields.items():
            if flatten_base and sub_dtype[0].shape:
                # If flatten_base is True, expand the shape into multiple dtypes
                for i in range(np.prod(sub_dtype[0].shape)):
                    flattened.append(np.dtype(sub_dtype[0].base))
            else:
                flattened.append(sub_dtype[0])
    else:
        # Handle regular dtype
        if flatten_base and ndtype.shape:
            for i in range(np.prod(ndtype.shape)):
                flattened.append(np.dtype(ndtype.base))
        else:
            flattened.append(ndtype)

    return flattened

