def _as_pairs(x, ndim, as_index=False):
    if x is None:
        x = np.empty((0,))
    else:
        x = np.asarray(x)

    if as_index:
        x = np.round(x).astype(np.intp)
        if np.any(x < 0):
            raise ValueError("All elements of x must be non-negative when as_index is True.")

    # Ensure x can be broadcasted to the shape (ndim, 2)
    try:
        pairs = np.broadcast_to(x, (ndim, 2))
    except ValueError:
        raise ValueError(f"x cannot be broadcasted to shape ({ndim}, 2).")

    return pairs

