def get_fieldstructure(adtype, lastname=None, parents=None):
    if parents is None:
        parents = {}
    if lastname is not None:
        if lastname not in parents:
            parents[lastname] = []

    if adtype.names is not None:
        for name, dtype in adtype.fields.items():
            if lastname is not None:
                parents[name] = [lastname]
            else:
                parents[name] = []
            get_fieldstructure(dtype[0], name, parents)

    return parents

