def parse_flags(line):
    d = {
        'include_dirs': [],
        'library_dirs': [],
        'libraries': [],
        'macros': [],
        'ignored': []
    }

    flags = line.split()

    for flag in flags:
        if flag.startswith('-I'):
            d['include_dirs'].append(flag[2:])
        elif flag.startswith('-L'):
            d['library_dirs'].append(flag[2:])
        elif flag.startswith('-l'):
            d['libraries'].append(flag[2:])
        elif flag.startswith('-D'):
            d['macros'].append(flag[2:])
        else:
            d['ignored'].append(flag)

    return d
