def check_ip_address(address, version=None):
    try:
        if version == 4:
            ipaddress.IPv4Address(address)
            return True
        elif version == 6:
            ipaddress.IPv6Address(address)
            return True
        elif version is None:
            # Check for both IPv4 and IPv6
            try:
                ipaddress.IPv4Address(address)
                return True
            except ipaddress.AddressValueError:
                try:
                    ipaddress.IPv6Address(address)
                    return True
                except ipaddress.AddressValueError:
                    return False
        else:
            return False
    except ipaddress.AddressValueError:
