def requires_application(backend=None, has=(), capable=(), force_gc=True):
    def decorator(test_func):
        @functools.wraps(test_func)
        def wrapper(*args, **kwargs):
            if force_gc:
                gc.collect()

            # Check if the required backend is available
            if backend and not use_app(backend):
                pytest.skip(f"Skipping test '{test_func.__name__}' because backend '{backend}' is not available.")

            # Check for required features
            for feature in has:
                if feature not in sys.modules:
                    pytest.skip(f"Skipping test '{test_func.__name__}' because required feature '{feature}' is not available.")

            # Check for required capabilities
            for capability in capable:
                if capability not in getattr(__main__, 'capabilities', []):
                    pytest.skip(f"Skipping test '{test_func.__name__}' because required capability '{capability}' is not available.")

            return test_func(*args, **kwargs)

        return wrapper
    return decorator

