from scipy.ndimage import zoom

def resize(image, shape, kind='linear'):
    # Calculate the zoom factors for each dimension
    zoom_factors = [shape[i] / image.shape[i] for i in range(len(shape))]

    # Determine the order of interpolation based on the kind
    if kind == 'linear':
        order = 1  # Linear interpolation
    elif kind == 'nearest':
        order = 0  # Nearest neighbor interpolation
    else:
        raise ValueError("Invalid interpolation method. Use 'linear' or 'nearest'.")

    # Resize the image using the zoom function
    scaled_image = zoom(image, zoom_factors, order=order)

    # Ensure the output image has np.float64 data type
    return scaled_image.astype(np.float64)

