def is_corpus(obj):
    # Check if obj is an iterable
    if not isinstance(obj, collections.abc.Iterable):
        return (False, obj)

    # Peek the first element
    iterator = iter(obj)
    try:
        first_element = next(iterator)
    except StopIteration:
        return (False, obj)  # Empty iterable is not a corpus

    # Check if the first element is an iterable
    if not isinstance(first_element, collections.abc.Iterable):
        return (False, obj)

    # Check if the first element contains exactly two items
