        lines.append(line.rstrip())
    if not has_prec_float:
        lines.insert(has_version, 'precision highp float;')
    if not has_prec_int:
        lines.insert(has_version, 'precision highp int;')
    if extensions:
        for ext_line in extensions:
            lines.insert(has_version, ext_line)
    return '\n'.join(lines)


